const modalOpen=document.querySelectorAll(".button-call-modal"),modal=document.querySelector(".modal"),modalClose=document.querySelector(".modal__close"),overlay=document.querySelector(".overlay");modalOpen.forEach(e=>{e.addEventListener("click",()=>{modal.classList.add("show"),overlay.classList.add("show")})}),modalClose.addEventListener("click",()=>{modal.classList.remove("show"),overlay.classList.remove("show")});